document.addEventListener("DOMContentLoaded",()=>{window.toggleMobileMenu=function(){const mobileNav=document.getElementById("mobileNav");const body=document.body;if(!mobileNav)return console.error("mobileNav manjka v HTML!");mobileNav.classList.toggle("active");if(mobileNav.classList.contains("active")){body.style.overflow="hidden"}else{body.style.overflow=""}};window.toggleDropdown=function(){const dropdown=document.getElementById("ctaDropdown");if(dropdown)dropdown.classList.toggle("active");};document.addEventListener("click",(event)=>{const dropdown=document.getElementById("ctaDropdown");const button=document.querySelector(".cta-button");if(!dropdown||!button)return;if(!dropdown.contains(event.target)&&!button.contains(event.target)){dropdown.classList.remove("active")}});const modal=document.getElementById("serviceModal");window.openModal=function(){if(modal)modal.classList.add("active");};window.closeModal=function(){if(modal)modal.classList.remove("active");};document.addEventListener("click",(event)=>{if(event.target===modal)window.closeModal();});const header=document.querySelector("header");window.addEventListener("scroll",()=>{if(!header)return;if(window.scrollY>50)header.classList.add("scrolled");else header.classList.remove("scrolled")});window.handleServiceSubmit=function(event){event.preventDefault();alert("Hvala! Vaše povpraševanje je bilo poslano.");window.closeModal();event.target.reset()};window.handleContactSubmit=function(event){event.preventDefault();alert("Hvala! Vaše sporočilo je bilo poslano.");event.target.reset()};const banner=document.getElementById("cookie-banner");const acceptBtn=document.getElementById("cookie-accept");if(banner&&acceptBtn){if(localStorage.getItem("cookieAccepted")==="yes"){banner.style.display="none"}
acceptBtn.addEventListener("click",()=>{localStorage.setItem("cookieAccepted","yes");banner.style.display="none"})}})